import axios from "axios"
import { useEffect, useState } from "react"

export default ()=>{
    const [userList, setUserList] = useState([]); //set state for userList
    const [name, setName] = useState("");         //set state for name
    const [email, setEmail] = useState("");       //set state for email
    const [phone, setPhone] = useState("");       //set state for phone

    useEffect(()=>{
        fetchData();

    },[]);
    const fetchData=()=>{
        axios.get("https://jsonplaceholder.typicode.com/users").then((response)=>{
            console.log(response);
            if(response && response.data){                          //method to fetch data from server
                setUserList(response.data)
            }

        })
    }

    const onNameChange=(event)=>{
        setName(event.target.value);
    }
    const onEmailChange=(event)=>{
        setEmail(event.target.value);
    }
    const onPhoneChange=(event)=>{
        setPhone(event.target.value);
    }
    const onSubmitClickHandler=(event)=>{     // submit activity
        event.preventDefault();
        console.log(name, email,phone);
        axios.post("https://jsonplaceholder.typicode.com/users", {name, email, phone, id:1,}).then((response)=>{
            console.log(response);
            alert("User Added")
            //fetch data
            fetchData();
            //reset input fields
            setName("");
            setEmail("");
            setPhone("");
        })
    }


    return(
        <>
        <h1>User List</h1>
        <form onSubmit={onSubmitClickHandler}>
            <label>Name</label>
            <input name="name" value={name} onChange={onNameChange}/>
            <label>Email</label>
            <input name="email" value={email} onChange={onEmailChange}/>
            <label>phone</label>
            <input name="phone" value={phone} onChange={onPhoneChange}/>
            <button type="submit">Submit</button>
        </form>
        <table class="table table-striped">
            <thead>
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>UserName</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>street</th>
                    <th>city</th>
                    <th>zipcode</th>
                    <th>website</th>
                    <th>Company name</th>
                </tr>
            </thead>
            <tbody>
                {userList.map((user, index)=>{       //mapping for fetched userList
                    return(
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.name}</td>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>{user.phone}</td>
                    <td>{user.address.street}</td>
                    <td>{user.address.city}</td>
                    <td>{user.address.zipcode}</td>
                    <td>{user.website}</td>
                    <td>{user.company.name}</td>
                  </tr>
                    )
                })}
            </tbody>
        </table>
        </>

    )
}
